select   datetime(AIRCRAFTSIGHTINGS.AIRCRAFTSIGHTINGEPOCH, 'unixepoch', 'localtime') as Seen, 
 AIRCRAFT.AIRCRAFTOWNER, AIRCRAFT.AIRCRAFTREGISTRATION, AIRCRAFT.AIRCRAFTTYPE, AIRCRAFTSIGHTINGS.AIRCRAFTSIGHTINGID from AIRCRAFTSIGHTINGS LEFT OUTER JOIN AIRCRAFT on
 AIRCRAFTSIGHTINGS.AIRCRAFTSIGHTINGID = AIRCRAFT.AIRCRAFTID where AIRCRAFT.AIRCRAFTOWNER LIKE "State Farm%"  group by AIRCRAFT.AIRCRAFTID, Seen order by datetime(AIRCRAFTSIGHTINGS.AIRCRAFTSIGHTINGEPOCH, 'unixepoch', 'localtime');